jQuery( document ).ready(function( $ ) 
{
    var crm_list;
    var crm_id = -1;
    var crm_name = '';
    var date_id = '2';      // Week To Date
    var from_date = '';
    var to_date = '';

    var aff = '';
    var f = '';
    var sf = '';

     //var spinner = '<p><div class="spinner"><div class="bounce1"></div><div class="bounce2"></div><div class="bounce3"></div></div></p>';
    var spinner = '<img src="../images/loading.gif" style="width:22px;height:22px;">';
    var read_mark = '<span class="glyphicon glyphicon-ok" aria-hidden="true"></span>';
    var close_mark = '<span class="glyphicon glyphicon-remove" aria-hidden="true" style="color: #ffa5a5"></span>';

    
    $.urlParam = function(name) {
        var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.href);
        if (results == null)
            return '';

        return results[1] || 0;
    }
    
    init();

    function init()
    {
        getInitialCrmID();
        setDateText();

        ajaxLoadSalesReport();
    }

    function show_alert(msg) 
    {
        $('.sales_alert').html(msg);
        $('.sales_alert').fadeIn(1000, function () {
            $('.sales_alert').fadeOut(3000);
        });
    }

    function show_waiting(show)
    {
        $('.sales_waiting').html(show ? spinner : '');
    }

    function getInitialCrmID()
    {
        if ($('.crm_dropdown_list').length > 0)
        {
            crm_id = $.urlParam('crm');
            //crm_id = $('.crm_dropdown_list').prop('id');
            //crm_name = $('.crm_dropdown_list').html();
            //
            $('.crm_dropdown_list').each( function(i) 
            {
                if (crm_id == '')
                {
                    crm_id = $(this).prop('id');
                    crm_name = $(this).html();
                    return false;
                }

                if (crm_id == $(this).prop('id'))
                {
                    crm_name = $(this).html();
                    return false;
                }
            });
        }

        if ($.urlParam('dt') != '')
            date_id = $.urlParam('dt');

        f = $.urlParam('f');
        sf = $.urlParam('sf');
    }

    // crm dropdown menu
    $('.crm_dropdown_menu li').on("click", function (e) 
    {
        crm_name = $(this).text();
        crm_id = $(this).find('a').attr('id');

        $('.crm_toggle_button').html(crm_name + " <span class=\"caret\"></span>");
    });

    // click data range
    $('input[name="start"]').datepicker({});
    $('input[name="end"]').datepicker({});

    $('.date_dropdown_menu li').on("click", function (e) 
    {
        var date_text = $(this).text();
        date_id = $(this).find('a').attr('id');

        $('.date_toggle_button').html(date_text + " <span class=\"caret\"></span>");

        setDateText();
    });

    $('.sales_search_button').on("click", function (e) 
    {
        ajaxLoadSalesReport();
    });

    // btn_export button
    $('.btn_export').click(function ()
    {
        from_date = $('#from_date').val();
        to_date = $('#to_date').val();
        aff = $.urlParam('aff');
        f = $.urlParam('f');
        sf = $.urlParam('sf');
        var cids = $('.search_campaign_ids').val();
        
        var url = './export_sales.php?crm_id=' + crm_id + '&crm_name=' + crm_name + '&fd=' + from_date + '&td=' + to_date + '&cids=' + cids + '&aff=' + aff + '&f=' + f + '&sf=' + sf;
        window.location.href = url;
    });

    $('.table_sales_body').on('click', '.btn_break', function (e)
    {
        if ($('#from_date').val() == '')
        {
            show_alert('Please select FROM DATE.');
            return;
        }

        if ($('#to_date').val() == '')
        {
            show_alert('Please select TO DATE.');
            return;
        }

        var fid = $(this).prop('id');

        from_date = $('#from_date').val();
        to_date = $('#to_date').val();
        aff = '1';

        if (fid.substring(0, 2) == 'f_')
        {
            f = fid.substring(2);
            sf = '';
        }
        else
        {
            f = $.urlParam('f');
            sf = fid.substring(2);
        }

        var url = './sales.php?crm=' + crm_id + '&dt=' + date_id +  '&fd=' + from_date + '&td=' + to_date + '&aff=' + aff + '&f=' + f + '&sf=' + sf;
        window.open(url, '_blank');
    });

    function ajaxLoadSalesReport()
    {
        if ($('#from_date').val() == '')
        {
            show_alert('Please select FROM DATE.');
            return;
        }

        if ($('#to_date').val() == '')
        {
            show_alert('Please select TO DATE.');
            return;
        }

        if (f != '')
            aff = '1';

        show_waiting(true);

        $.ajax(
        {
            type : 'GET',
            url : '../daemon/ajax_admin/sales_list.php',
            data : {
                'crm_id' : crm_id,
                'from_date' : $('#from_date').val(),
                'to_date' : $('#to_date').val(),
                'campaign_id' : $('.search_campaign_ids').val(), 
                'aff' : aff,
                'f' : f,
                'sf' : sf
            },
            success:function(response)
            {
                show_waiting(false);

                var obj = jQuery.parseJSON(response);
                var html = '';
                
                if (obj[0] == 'error')
                {
                    show_alert('Cannot load sales report.');
                }
                else if (obj[0] == 'no_cookie')
                {
                    window.location.href = '../../admin/login.php';
                    return;
                }
                else
                {
                    for (var i = 0; i < obj[2].length; i ++)
                    {
                        html += '<tr>';
                        if (obj[2][i][0] == 'Total')
                            html += '<td>' + obj[2][i][0] + '</td>';
                        else
                        {
                            if (obj[2][i][8] == 'Affiliate')
                                html += '<td>(' + obj[2][i][0] + ') ' + obj[2][i][9] + '</td>';
                            else
                                html += '<td>' + obj[2][i][0] + '</td>';
                        }
                        html += '<td>' + obj[2][i][1] + '</td>';
                        html += '<td>' + obj[2][i][2] + '</td>';
                        html += '<td>' + obj[2][i][3] + '</td>';
                        if (obj[2][i][6] == '')
                            html += '<td></td>';
                        else
                        {
                            if (obj[2][i][8] == 'Affiliate')
                                html += '<td><button type="button" class="btn btn-success btn-sm btn_break" id="f_' + obj[2][i][6] + '">Affiliate ID</button></td>';
                            else
                                html += '<td><button type="button" class="btn btn-success btn-sm btn_break" id="s_' + obj[2][i][7] + ':' + obj[2][i][6] + '">Sub-Affiliate ID</button></td>';
                        }
                        html += '</tr>';
                    }
                }

                $('.table_sales_body').html(html);
            },
            failure:function(response) 
            {
                show_alert('Cannot load sales report.');
            }
        });
    }

    function setDateText()
    {
        $('#from_date').prop('disabled', true);
        $('#to_date').prop('disabled', true);

        var date = new Date();
        var today = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());

        if (date_id == '0')        // Today
        {
            from_date = today;
            to_date = today;
        }
        else if (date_id == '1')        // Yesterday
        {
            date.setDate(date.getDate() - 1);

            from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
            to_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        }
        else if (date_id == '2')        // Week To Date
        {
            var firstday = date.getDate() + 1;
            if (date.getDay() == 0)
                firstday -= 7;
            else
                firstday -= date.getDay();

            date.setDate(firstday);

            from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
            to_date = today;
        }
        else if (date_id == '3')        // Month To Date
        {
            from_date = getDateText(date.getFullYear(), date.getMonth() + 1, 1);
            to_date = today;
        }
        else if (date_id == '4')        // Year To Date
        {
            from_date = getDateText(date.getFullYear(), 1, 1);
            to_date = today;
        }
        else if (date_id == '5')        // Last Week
        {
            var firstday = date.getDate() + 1 - 7;
            if (date.getDay() == 0)
                firstday -= 7;
            else
                firstday -= date.getDay();

            date.setDate(firstday);
            from_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());

            firstday = date.getDate() + 6;
            date.setDate(firstday);
            to_date = getDateText(date.getFullYear(), date.getMonth() + 1, date.getDate());
        }
        else if (date_id == '6')        // Custom
        {
            from_date = '';
            to_date = '';

            $('#from_date').prop('disabled', false);
            $('#to_date').prop('disabled', false);
        }

        if ($.urlParam('fd') == '')
            $('#from_date').val(from_date);
        else
            $('#from_date').val($.urlParam('fd'));

        if ($.urlParam('td') == '')
            $('#to_date').val(to_date);
        else
            $('#to_date').val($.urlParam('td'));
    }

    function getDateText(year, month, day)
    {
        var dd = day;
        var mm = month;
        var yyyy = year;

        if (dd < 10)
            dd = '0' + dd;

        if (mm < 10)
            mm = '0' + mm;

        return (mm + '/' + dd + '/' + yyyy);
    }
});